// LoansPage.tsx

import React, { useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Button,
  Popover,
  IconButton,
} from "@mui/material";
import InfoIcon from "@mui/icons-material/Info";
import "./LoansPage.css";

const loans = [
  // This is just dummy data, replace it with your actual data
  {
    id: 1,
    bookCover: "cover1.jpg",
    status: "Borrowed",
    borrowedDate: "2022-01-01",
    returnDate: "2022-01-31",
    bookDetails: "Book details...",
  },
  {
    id: 2,
    bookCover: "cover2.jpg",
    status: "Borrowed",
    borrowedDate: "2022-02-01",
    returnDate: "2022-02-28",
    bookDetails: "Book details...",
  },
  // ...
];

const LoansPage: React.FC = () => {
  const [anchorEl, setAnchorEl] = useState<HTMLButtonElement | null>(null);
  const [selectedLoan, setSelectedLoan] = useState<any>(null);

  const handleReturn = (id: number) => {
    // Handle the return logic here
    console.log(`Return book with id ${id}`);
  };

  const handlePopoverOpen = (
    event: React.MouseEvent<HTMLButtonElement>,
    loan: any,
  ) => {
    setAnchorEl(event.currentTarget);
    setSelectedLoan(loan);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
    setSelectedLoan(null);
  };

  const open = Boolean(anchorEl);

  return (
    <div className="loans-page">
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div">
            My Library
          </Typography>
        </Toolbar>
      </AppBar>

      {loans.map((loan) => (
        <Card key={loan.id} className="loan-card">
          <div className="book-cover">
            <CardMedia
              component="img"
              image={loan.bookCover}
              alt="Book cover"
            />
          </div>
          <CardContent className="loan-info">
            <Typography variant="subtitle1" color="text.secondary">
              Status: {loan.status}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Borrowed: {loan.borrowedDate}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Return: {loan.returnDate}
            </Typography>
          </CardContent>
          <IconButton
            onClick={(event) => handlePopoverOpen(event, loan)}
            aria-label="show book details"
          >
            <InfoIcon />
          </IconButton>
          <Popover
            open={open}
            anchorEl={anchorEl}
            onClose={handlePopoverClose}
            anchorOrigin={{
              vertical: "center",
              horizontal: "right",
            }}
            transformOrigin={{
              vertical: "center",
              horizontal: "left",
            }}
          >
            <Typography sx={{ p: 2 }}>{selectedLoan?.bookDetails}</Typography>
          </Popover>
          <Button onClick={() => handleReturn(loan.id)} variant="outlined">
            Return
          </Button>
        </Card>
      ))}
    </div>
  );
};

export default LoansPage;
