// LoansPage.tsx

import React from "react";
import "./Loans-page.css";

const loans = [
  // This is just dummy data, replace it with your actual data
  {
    id: 1,
    bookCover: "cover1.jpg",
    status: "Borrowed",
    borrowedDate: "2022-01-01",
    returnDate: "2022-01-31",
  },
  {
    id: 2,
    bookCover: "cover2.jpg",
    status: "Borrowed",
    borrowedDate: "2022-02-01",
    returnDate: "2022-02-28",
  },
  // ...
];

const LoansPage: React.FC = () => {
  const handleReturn = (id: number) => {
    // Handle the return logic here
    console.log(`Return book with id ${id}`);
  };

  return (
    <div className="loans-page">
      {loans.map((loan) => (
        <div key={loan.id} className="loan-card">
          <img src={loan.bookCover} alt="Book cover" className="book-cover" />
          <div className="loan-info">
            <p>Status: {loan.status}</p>
            <p>Borrowed: {loan.borrowedDate}</p>
            <p>Return: {loan.returnDate}</p>
            <button onClick={() => handleReturn(loan.id)}>Return</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default LoansPage;
