import React from "react";
import "./Loans-page.css";
import {
  AppBar,
  Toolbar,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";

const loans = [
  // This is just dummy data, replace it with your actual data
  {
    loanId: 1,
    coverImageUrl:
      "https://media.harrypotterfanzone.com/sorcerers-stone-us-childrens-edition.jpg",
    status: "Borrowed",
    dueDate: "2022-01-31",
    loanDate: "2022-01-01",
    returnDate: "2022-01-31",
  },
  {
    loanId: 2,
    coverImageUrl:
      "https://media.harrypotterfanzone.com/chamber-of-secrets-uk-childrens-edition-2014.jpg",
    status: "Borrowed",
    loanDate: "2022-02-01",
    dueDate: "2022-02-28",
    returnDate: "2022-02-28",
  },
  // ...
];

const LoansPage: React.FC = () => {
  const handleReturn = (id: number) => {
    // Handle the return logic here
    console.log(`Return book with id ${id}`);
  };

  return (
    <div>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div">
            My Library
          </Typography>
        </Toolbar>
      </AppBar>
      {loans.map((loan) => (
        <Card key={loan.loanId} className="loan-card">
          <CardMedia
            component="img"
            sx={{ width: 151 }}
            image={loan.coverImageUrl}
            alt="Book cover"
          />
          <CardContent className="loan-info">
            <Typography variant="subtitle1" color="text.secondary">
              Status: {loan.status}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Borrowed: {loan.loanDate}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Return: {loan.returnDate}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Due date: {loan.dueDate}
            </Typography>
            <Button
              onClick={() => handleReturn(loan.loanId)}
              variant="outlined"
              sx={{ marginTop: "auto" }}
            >
              Return
            </Button>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default LoansPage;
