import React from "react";
import "./Loans-page.css";
import {
  AppBar,
  Toolbar,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";

const loans = [
  // This is just dummy data, replace it with your actual data
  {
    id: 1,
    bookCover: "cover1.jpg",
    status: "Borrowed",
    borrowedDate: "2022-01-01",
    returnDate: "2022-01-31",
  },
  {
    id: 2,
    bookCover: "cover2.jpg",
    status: "Borrowed",
    borrowedDate: "2022-02-01",
    returnDate: "2022-02-28",
  },
  // ...
];

const LoansPage: React.FC = () => {
  const handleReturn = (id: number) => {
    // Handle the return logic here
    console.log(`Return book with id ${id}`);
  };

  return (
    <div>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div">
            My Library
          </Typography>
        </Toolbar>
      </AppBar>
      {loans.map((loan) => (
        <Card key={loan.id} sx={{ display: "flex", marginBottom: 2 }}>
          <CardMedia
            component="img"
            sx={{ width: 151 }}
            image={loan.bookCover}
            alt="Book cover"
          />
          <CardContent sx={{ display: "flex", flexDirection: "column" }}>
            <Typography variant="subtitle1" color="text.secondary">
              Status: {loan.status}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Borrowed: {loan.borrowedDate}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Return: {loan.returnDate}
            </Typography>
            <Button
              onClick={() => handleReturn(loan.id)}
              variant="outlined"
              sx={{ marginTop: "auto" }}
            >
              Return
            </Button>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};

export default LoansPage;
