  return (
    <div>
      <AppBarUser />
      {Object.keys(sortedBooks).map((genre, genreIndex) => (
        <div key={genreIndex}>
          <Typography variant="h4" component="h2" className="section-title">
            {genre}
          </Typography>
          <Grid
            container
            justifyContent="center"
            spacing={3}
            className="book-list"
          >
            {sortedBooks[genre].map((book, bookIndex) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={bookIndex}>
                <Card className="book-card">
                  <CardContent>
                    <Typography
                      variant="h5"
                      component="h2"
                      className="book-title"
                    >
                      {book.title}
                    </Typography>
                    <Typography className="book-author">
                      {book.author}
                    </Typography>
                    <img
                      className="book-cover"
                      src={book.coverImageUrl}
                      alt={book.title}
                      style={{ width: "100%", height: "auto" }}
                    />
                    <button onClick={() => handleOpenBook(book)}>
                      Open Book
                    </button>
                  </CardContent>
                  <Accordion>
                    <AccordionSummary
                      expandIcon={<ExpandMoreIcon />}
                      aria-controls="panel1a-content"
                      id="panel1a-header"
                    >
                      <Typography>Details</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      <Typography>
                        Year Published: {book.yearPublished} <br />
                        Genre: {book.genre} <br />
                        Summary: {book.summary} <br />
                      </Typography>
                    </AccordionDetails>
                  </Accordion>
                </Card>
              </Grid>
            ))}
          </Grid>
        </div>
      ))}
      <Modal />
    </div>
  );
};

export default HomePage;
