import React from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";
import "./Home-page.css";

const HomePage: React.FC = () => {
  const initialValues = {
    bookTitle: "",
    bookAuthor: "",
    bookDescription: "",
  };

  const validationSchema = Yup.object({
    bookTitle: Yup.string().required("Required"),
    bookAuthor: Yup.string().required("Required"),
    bookDescription: Yup.string().required("Required"),
  });

  return (
    <div>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={(values, { setSubmitting }) => {
          console.log(values);
          setSubmitting(false);
        }}
      >
        <Form className="home-form">
          <ul className="book-list">
            <li className="book-list-item">
              <img
                src="book-image-url"
                alt="Book Cover"
                className="book-cover"
              />{" "}
              {}
              <div className="book-details">
                <label htmlFor="bookTitle" className="book-label">
                  Book Title
                </label>{" "}
                {}
                <Field name="bookTitle" type="text" className="book-title" />
                <ErrorMessage
                  name="bookTitle"
                  component="div"
                  className="error-message"
                />{" "}
                {}
                <label htmlFor="bookAuthor" className="book-label">
                  Book Author
                </label>{" "}
                {}
                <Field name="bookAuthor" type="text" className="book-author" />
                <ErrorMessage
                  name="bookAuthor"
                  component="div"
                  className="error-message"
                />{" "}
                {}
                <label htmlFor="bookDescription" className="book-label">
                  Book Description
                </label>{" "}
                {}
                <Field
                  name="bookDescription"
                  type="text"
                  className="book-description"
                />
                <ErrorMessage
                  name="bookDescription"
                  component="div"
                  className="error-message"
                />{" "}
                {}
              </div>
            </li>
            {/* Add more book list items here */}
          </ul>
          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </div>
  );
};

export default HomePage;
