import React, { useState } from "react";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Accordion,
  AccordionSummary,
  AccordionDetails,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import "./styles/ManageLoans.css";

const initialLoans = [
  {
    loanId: 1,
    coverImageUrl:
      "https://media.harrypotterfanzone.com/sorcerers-stone-us-childrens-edition.jpg",
    status: "Borrowed",
    dueDate: "2022-01-31",
    loanDate: "2022-01-01",
    returnDate: "2022-01-31",
    bookDetails: { title: "Philosopher's Stone", author: "J.K. Rowling" },
  },
  // ... more loan entries ...
];

const ManageLoans: React.FC = () => {
  const [loans, setLoans] = useState(initialLoans);

  const handleReturn = (loanId: number) => {
    setLoans(loans.filter((loan) => loan.loanId !== loanId));
  };

  return (
    <div className="manage-loans">
      {loans.map((loan) => (
        <Card key={loan.loanId} className="loan-card">
          <CardMedia
            component="img"
            height="140"
            image={loan.coverImageUrl}
            alt={loan.bookDetails.title}
          />
          <CardContent>
            <Typography variant="h5">{loan.bookDetails.title}</Typography>
            <Typography variant="body1">{loan.bookDetails.author}</Typography>
            <Typography variant="body2">Status: {loan.status}</Typography>
            <Typography variant="body2">Due Date: {loan.dueDate}</Typography>
            <Typography variant="body2">Loan Date: {loan.loanDate}</Typography>
            <Typography variant="body2">
              Return Date: {loan.returnDate}
            </Typography>
            <Button onClick={() => handleReturn(loan.loanId)}>Return</Button>
          </CardContent>
        </Card>
      ))}
    </div>
  );
};
