import React from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";

const HomePage: React.FC = () => {
  const initialValues = {
    bookTitle: "",
    bookAuthor: "",
    bookDescription: "",
  };

  const validationSchema = Yup.object({
    bookTitle: Yup.string().required("Required"),
    bookAuthor: Yup.string().required("Required"),
    bookDescription: Yup.string().required("Required"),
  });

  return (
    <div>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={(values, { setSubmitting }) => {
          // Handle form submission here
          console.log(values);
          setSubmitting(false);
        }}
      >
        <Form>
          <ul className="book-list">
            <li className="book-list-item">
              <img src="book-image-url" alt="Book Cover" />
              <div className="book-details">
                <label htmlFor="bookTitle">Book Title</label>
                <Field name="bookTitle" type="text" className="book-title" />
                <ErrorMessage name="bookTitle" component="div" />

                <label htmlFor="bookAuthor">Book Author</label>
                <Field name="bookAuthor" type="text" className="book-author" />
                <ErrorMessage name="bookAuthor" component="div" />

                <label htmlFor="bookDescription">Book Description</label>
                <Field
                  name="bookDescription"
                  type="text"
                  className="book-description"
                />
                <ErrorMessage name="bookDescription" component="div" />
              </div>
            </li>
            {/* Add more book list items here */}
          </ul>
          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </div>
  );
};

export default HomePage;
