import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import "./AdminConsole.css";

// Define the types for the form data
interface UserFormValues {
  username: string;
}

interface BookFormValues {
  title: string;
  author: string;
}

interface LoanFormValues {
  userId: string;
  bookId: string;
}

const AdminConsole: React.FC = () => {
  const [users, setUsers] = useState<string[]>([]);
  const [books, setBooks] = useState<{ title: string; author: string }[]>([]);
  const [loans, setLoans] = useState<{ userId: string; bookId: string }[]>([]);

  const addUser = (values: UserFormValues) => {
    setUsers([...users, values.username]);
  };

  const deleteUser = (username: string) => {
    setUsers(users.filter((user) => user !== username));
  };

  const addBook = (values: BookFormValues) => {
    setBooks([...books, { title: values.title, author: values.author }]);
  };

  const deleteBook = (title: string) => {
    setBooks(books.filter((book) => book.title !== title));
  };

  const processLoan = (values: LoanFormValues) => {
    setLoans([...loans, { userId: values.userId, bookId: values.bookId }]);
  };

  const processReturn = (userId: string, bookId: string) => {
    setLoans(
      loans.filter(
        (loan) => !(loan.userId === userId && loan.bookId === bookId),
      ),
    );
  };

  return (
    <div className="admin-console">
      <h1>Admin Console</h1>

      <section>
        <h2>Add User</h2>
        <Formik
          initialValues={{ username: "" }}
          validationSchema={Yup.object({
            username: Yup.string().required("Required"),
          })}
          onSubmit={(values, { resetForm }) => {
            addUser(values);
            resetForm();
          }}
        >
          <Form>
            <Field name="username" type="text" placeholder="Username" />
            <ErrorMessage name="username" component="div" />
            <button type="submit">Add User</button>
          </Form>
        </Formik>

        <h2>Delete User</h2>
        <ul>
          {users.map((user) => (
            <li key={user}>
              {user}
              <button onClick={() => deleteUser(user)}>Delete</button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h2>Add Book</h2>
        <Formik
          initialValues={{ title: "", author: "" }}
          validationSchema={Yup.object({
            title: Yup.string().required("Required"),
            author: Yup.string().required("Required"),
          })}
          onSubmit={(values, { resetForm }) => {
            addBook(values);
            resetForm();
          }}
        >
          <Form>
            <Field name="title" type="text" placeholder="Title" />
            <ErrorMessage name="title" component="div" />
            <Field name="author" type="text" placeholder="Author" />
            <ErrorMessage name="author" component="div" />
            <button type="submit">Add Book</button>
          </Form>
        </Formik>

        <h2>Delete Book</h2>
        <ul>
          {books.map((book) => (
            <li key={book.title}>
              {book.title} by {book.author}
              <button onClick={() => deleteBook(book.title)}>Delete</button>
            </li>
          ))}
        </ul>
      </section>

      <section>
        <h2>Process Loan</h2>
        <Formik
          initialValues={{ userId: "", bookId: "" }}
          validationSchema={Yup.object({
            userId: Yup.string().required("Required"),
            bookId: Yup.string().required("Required"),
          })}
          onSubmit={(values, { resetForm }) => {
            processLoan(values);
            resetForm();
          }}
        >
          <Form>
            <Field name="userId" type="text" placeholder="User ID" />
            <ErrorMessage name="userId" component="div" />
            <Field name="bookId" type="text" placeholder="Book ID" />
            <ErrorMessage name="bookId" component="div" />
            <button type="submit">Process Loan</button>
          </Form>
        </Formik>

        <h2>Process Return</h2>
        <ul>
          {loans.map((loan, index) => (
            <li key={index}>
              User: {loan.userId} borrowed Book: {loan.bookId}
              <button onClick={() => processReturn(loan.userId, loan.bookId)}>
                Return
              </button>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default AdminConsole;
